package com.simple.SpringBoot.Controller;import com.simple.SpringBoot.Model.Request.CreateUserRequest;import com.simple.SpringBoot.Model.Request.UpdateUserRequest;import com.simple.SpringBoot.Model.Response.SearchUserResponse;import com.simple.SpringBoot.Model.Response.UserInfoResponse;import com.simple.SpringBoot.Service.UserService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.web.bind.annotation.*;@RestController@RequestMapping("/user")public class UserController {    private final UserService userService;    @Autowired    public UserController(UserService userService) {        this.userService = userService;    }    @GetMapping("/findAll")    public SearchUserResponse findAll() {        return new SearchUserResponse(userService.findAllUser());    }    @GetMapping("/findById")    public UserInfoResponse findById(int id) {        return new UserInfoResponse(userService.findUserById(id));    }    @PostMapping("/create")    public UserInfoResponse createUser(@RequestBody CreateUserRequest newUser) {        return new UserInfoResponse(userService.createUser(newUser.toUser()));    }    @PutMapping("/update/{userId}")    public UserInfoResponse updateUser(@PathVariable int userId, @RequestBody UpdateUserRequest updateUser) {        return new UserInfoResponse(userService.updateUser(userId, updateUser.toUser()));    }    @DeleteMapping("/delete/{userId}")    public String deleteUser(@PathVariable int userId) {        return userService.deleteUser(userId);    }}